mapper.select:
select ${columns}
from ${table}
where 1 = 1
	 AND ${k1} = #{v1}
	 AND ${k2} = #{v2}
	 AND ${k3} = #{v3}
	 AND ${k4} = #{v4}
	 AND ${k5} = #{v5}
	 ${whereStr} 
 order by ${orderStr}

select count(1)
from ${table}
where 1 = 1
	 AND ${k1} = #{v1}
	 AND ${k2} = #{v2}
	 AND ${k3} = #{v3}
	 AND ${k4} = #{v4}
	 AND ${k5} = #{v5}

mapper.delete:
delete from ${table}
where 1 = 1
	 AND ${k1} = #{v1}
	 AND ${k2} = #{v2}
	 AND ${k3} = #{v3}
	 AND ${k4} = #{v4}
	 AND ${k5} = #{v5}
	 AND ${in1} in ${inv1}
	 AND ${in2} in ${inv2}
